import { useLoaderData } from "react-router-dom";
import { FileType, homeLoader } from "./types";
import { option } from "./constant";
import { EchartBoard, HomeCard, MySwiper } from "./styles";
import {SwiperSlide } from "swiper/react";

import "swiper/css";
import { BookCard } from "../BookList/styles";
import { Button, Form, InputNumber, Modal, Progress } from "antd";
import Meta from "antd/es/card/Meta";
import useHome from "./useHome";

export const HomePage = () => {

  const {clickProcess, openModal,
  summaryData, readingBook,editBookId,
  handleOk,handleCancel, form, onFinish} = useHome();
  const errorImg = "data:image/png;base64,";


  return (
    <>
      <MySwiper
        slidesPerView={3}
        spaceBetween={0}
      >
        {readingBook.map((book) => (
          <SwiperSlide key={book._id}>
            <HomeCard
              $color={book.status == "READED" ? "green" : book.status == "UNREAD" ? "gray" : "red"}
              hoverable
              style={{width: 180}}
              cover={
                <img 
                  alt={book.title} 
                  src={book.cover ?? errorImg} 
                  onError={({currentTarget}) => {
                    const regex = /\/\/images.weserv.nl\/*/g
                    if ( (!currentTarget.src) || currentTarget.src.match(regex)) {
                      currentTarget.onerror = null;
                      currentTarget.src = errorImg;
                    } else {
                      currentTarget.src = currentTarget.src.replace("", "//images.weserv.nl/?url=");
                    }
                  }}
                />
              }
              actions={[
                <div
                  onClick={() => {
                    clickProcess(book._id);
                  }}
                >
                  {book.currentPages && <div>{`${book.currentPages} / ${book.pages}`}</div>}
                  <Progress percent={(book.currentPages ?? 0) / (book.pages ?? 1) * 100} showInfo={false} />
                </div>
              ]}
            >
              <Meta 
                title={book.title} 
                description={
                  <>
                    <div>{book.author.map((role) => role.full_name).join(" & ")}</div>
                    
                  </>
                }
              />
            </HomeCard>
          </SwiperSlide>
        ))}
      </MySwiper>
      <Modal
        title={editBookId}
        open = {openModal}
        onOk={handleOk}
        onCancel={handleCancel}
        onClose={handleCancel}
      >
        <Form
          form={form}
          onFinish={onFinish}
        >
          <Form.Item<FileType>
            label="Pages"
            name="pages"
            initialValue={readingBook.find(item => item._id == editBookId)?.pages ?? 0}
          >
            <InputNumber min={0} />
          </Form.Item>
          <Form.Item<FileType>
            label="CurrentPages"
            name="currentPages"
            initialValue={readingBook.find(item => item._id == editBookId)?.currentPages ?? 0}
          >
            <InputNumber min={0} />
          </Form.Item>
        </Form>
      </Modal>
      <EchartBoard option={option(summaryData)} />
    </>
  );
}